require_relative '../app/information_card'
require_relative '../app/scrape_content'
require 'rspec'

RSpec.describe ScrapeContent do

  describe 'Test for Van-Goph Paintings' do
    let(:html_file_path) { 'spec/fixtures/van-gogh-paintings.html' }
    let(:expected_output) do
      { 
        'name' => 'The Starry Night',
        'extensions' => ["1889"],
        'link' => "https://www.google.com/search?gl=us&hl=en&q=The+Starry+Night&stick=H4sIAAAAAAAAAONgFuLQz9U3MI_PNVLiBLFMzC3jC7WUspOt9Msyi0sTc-ITi0qQmJnFJVbl-UXZxY8YI7kFXv64JywVMGnNyWuMXlxEaBJS4WJzzSvJLKkUkuLikYLbrcEgxcUF5_EsYhUIyUhVCC5JLCqqVPDLTM8oAQDmNFnDqgAAAA&npsic=0&sa=X&ved=0ahUKEwiL2_Hon4_hAhXNZt4KHTOAACwQ-BYILw",
        'image' => 'data:image/jpeg;base64,'
      }
    end
  
    let(:output_file_path) { ScrapeContent.new(file_path: html_file_path).call }
  
    it 'Validates output' do
      output = JSON.parse(File.open(output_file_path).read)
      expect(output['artworks'].first).to eq(expected_output)
    end
  
    after do
      File.delete(output_file_path) if File.exists?(output_file_path)
    end
  end

